import './css/App.css';
import jwt from 'jsonwebtoken';
import axios from 'axios';
import React, { useState, useEffect} from 'react';
import Router from "./routes/Router";
import { BrowserRouter } from 'react-router-dom';
import Navbar from './components/Navbar';
import { useLocalStorage } from './utils/hooks';
import { UserContext } from './utils/UserContext';

function App() {
    const [token, setToken] = useLocalStorage('token', null)
    const [currentUser, setCurrentUser] = useState(null)

    // on token change, get user info and store in current user
    useEffect(() => {
      async function getUser() {
        try {
          const payload = jwt.decode(token)
          const username = payload.username
          const user = await axios.get(`/users/${username}`)
          setCurrentUser(user)
        } catch {
          return
        }
      }
      if (token) getUser()
    }, [token])

   // get token, set token state 
   async function login(username, password) {
      const token = await JoblyApi.getToken(username, password) 
      if (!token) return 
      setToken(token)
    }

    // clear user from state and token in localStorage
    function logout() {
      setCurrentUser(null)
      setToken(null)
    }

    // register new user and store their token in localStorage
    async function signup(user) {
      const token = await JoblyApi.register(user)
      if (!token) return
      setToken(token)
     }
  return (
    <div className="App">
        <BrowserRouter>
            <UserContext.Provider value={{login, logout, signup, currentUser, setCurrentUser, token}}>
                <Navbar />
                <Router />
            </UserContext.Provider>
        </BrowserRouter>
    </div>
  );
}

export default App;
