import React from 'react';

const Reply = ({reply}) => {
    return (
        <div className="Reply">
            <div className="Reply-posted-by">{reply.postedBy}</div>
            <div className="Reply-date-posted">{reply.datePosted}</div>
            <div className="Reply-text">{reply.text}</div>
        </div>
    )
}

export default Reply;
