import React, { useContext, useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import UserContext from '../utils/UserContext';
import '../css/UserSets.css';
import SetCard from './SetCard';


const UserSets = () => {
    const { currentUser, setCurrentUser, request } = useContext(UserContext)
    const [sets, setSets] = useState(currentUser.sets)

    // useEffect(() => {
    //     const refreshUser = async () => {
    //         const resp = await request(`users/${currentUser.username}`)
    //         setCurrentUser(resp.user)
    //         setSets(currentUser.sets)
    //     }
    //     refreshUser()
    // }, [sets])

    //
    // const sets = currentUser.sets
    // how to update when new set is created and deleted?
    return (
        <>
            <div className="NewSetCard">
                <NavLink state={{sets: sets}} to="/flashcards/sets/new">
                    <button className="NewSetButton">+</button>
                    <p className="NewSetText" >Create a new set</p>
                </NavLink>
            </div>
            <div>
                {sets.length === 0 ? 
                <p className="NoNewSets"><i>No sets yet!</i></p>
                :
                sets.map(s => <SetCard key={s.id} set={s} />)}
            </div>
        </>
    )
}

export default UserSets;
