import UserContext from '../utils/UserContext';

const NewPostForm = () => {
    const INITIAL_STATE = {
        text: ''
    }
    const [formData, handleChange] = useFields(INITIAL_STATE)
    return (
       <>
        <form className="NewPostForm">
            <div className="NewPostForm-username">
                <p>{currentUser.username}</p>
            </div>
            <input type="text" name="text" value={formData.text} onChange={handleChange} placeholder="Make a post"></input>
            <button>Post</button>
        </form>
       </>
    )
}

export default NewPostForm;
