import React, { useContext, useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import UserContext from '../utils/UserContext';
import "../css/Set.css";
import SplitFlashcard from './SplitFlashcard';
import SetEditCard from './SetEditCard';
import NewFlashcardForm from './NewFlashcardForm';


const Set =  () => {
    const { id } = useParams()
    const { request, currentUser, setCurrentUser } = useContext(UserContext)
    const [set, setSet] = useState()
    const navigate = useNavigate()
    const [flashcards, setFlashcards] = useState([])
    const [editMode, setEditMode] = useState(false)
    let newFlashcards = []

    // get set data on load
    useEffect(() => {
        const getSet = async () => {
            const resp = await request(`sets/${id}`)
            setSet(resp.set)
            setFlashcards(resp.set.flashcards)
        }
        getSet()
    }, [editMode])

    const toggleEditMode = () => {
        editMode ? setEditMode(false) : setEditMode(true)
    }

    const deleteSet = async () => {
        await request(`sets/${id}`, {}, 'delete')
        const user = await request(`users/${currentUser.username}`)
        setCurrentUser(user.user)
        navigate(`/flashcards`)
    }

    // add flashcard
    const addFlashcard = () => {
        newFlashcards.push(<NewFlashcardForm sideOneText={sideOneText} sideTwoText={sideTwoText}/>)
        return
    }
    // practice

    return (
        <>
        {set ? 
        !editMode ? 
        <div>
            <div className="Set">
                <p className="Set-name">{set.name}</p>
                <p className="Set-description">{set.description}</p>
                {set.createdBy === currentUser.username ? 
                <div className="Set-buttons">
                    <button onClick={() => toggleEditMode(editMode)}>Edit Set</button>
                    <button className="delete-button" onClick={deleteSet}>Delete Set</button>
                </div>
                : '' }
            </div>
        </div>
        : 
        <SetEditCard set={set} toggleEditMode={toggleEditMode} />
        : 
        <h1 className="Set-h1"> Loading set... </h1> }

        { flashcards.length > 0 ? 
            <div className="Flashcard-container">
                {flashcards.map(f => <SplitFlashcard key={f.id} flashcard={f} flashcards={flashcards} sideOneName={set.sideOneName} sideTwoName={set.sideTwoName} setFlashcards={setFlashcards}/>)}
            </div>
        :  
           <h2 className="Flashcards-h2"> <br/> <br/>No flashcards yet!</h2>}

        <button className="Set-buttons-practice">Practice</button>
        <button className="Set-buttons-add-flashcard" onClick={addFlashcard}>Add Flashcard</button>
        </>

    )
}

export default Set;
