import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import UserContext from '../utils/UserContext';
import '../css/Flashcard.css';

const FlashcardPractice = () => {
    const { id } = useParams()
    const { request } = useContext(UserContext)
    const [set, setSet] = useState()
    const [flashcards, setFlashcards] = useState([])
    const [currentFlashcard, setCurrentFlashcard] = useState(null)
    const [currentSide, setCurrentSide] = useState(1)

    // get set and flashcards on load
    useEffect(() => {
        const getSet = async () => {
            const resp = await request(`sets/${id}`)
            setSet(resp.set)  
            setFlashcards(resp.set.flashcards)
            if (resp.set.flashcards.length > 0) setCurrentFlashcard(resp.set.flashcards[0])
        }
        getSet()
    }, [])




    return (
        <> 
        {set && flashcards ? 
            <div>
                <h1>{set.name}</h1>
                <h2>{set.description}</h2>

                <div className="Flashcard">
                    {currentFlashcard.sideOneImageUrl !== '' ? 
                        <img src={currentFlashcard.sideOneImageUrl}></img>
                    : ''}
                    <p className="Flashcard-text">{currentFlashcard.sideOneText}</p>
                </div>
                <div className="Flashcard">

                </div>
            </div> 
            :
            <h1 className="Flashcard-h1">Loading set...</h1>
            } 
        </>
    )
}

export default FlashcardPractice;
