import React, { useContext } from 'react';
import { useFields } from '../utils/hooks';
import '../css/NewPostForm.css';
import UserContext from '../utils/UserContext';

const NewPostForm = () => {
    const { currentUser } = useContext(UserContext)
    const INITIAL_STATE = {
        text: ''
    }
    const [formData, handleChange] = useFields(INITIAL_STATE)
    return (
       <>
        <form className="NewPostForm">
            <div>
                <textarea className="NewPostForm-input" type="textarea" name="text" value={formData.text} onChange={handleChange} placeholder="Make a post"></textarea>
            </div>
            <div>
                <button className="NewPostForm-post-button">Post</button>
            </div>
        </form>
       </>
    )
}

export default NewPostForm;
