import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import UserContext from '../utils/UserContext';


const Set =  () => {
    const { id } = useParams()
    const { request } = useContext(UserContext)
    const [set, setSet] = useState()
    const [flashcards, setFlashcards] = useState([])

    useEffect(() => {
        const getSet = async () => {
            const set = await request(`sets/${id}`)
            setSet(set)
        }
        getSet()
        console.log(set)
    }, [])



    // get set with that id
    // informational card on top
    // map flashcards into cards
    return (
        <>
            {set ? console.log(set) : 'Loading...'}

        </>
    )
}

export default Set;
