import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import UserContext from '../utils/UserContext';
import '../css/GroupPage.css';
import GroupTabs from './GroupTabs';

const GroupPage = () => {
    const { id } = useParams()
    const [group, setGroup] = useState()
    const { request, currentUser } = useContext(UserContext)

    // get group on page load
    useEffect(() => {
        const getGroup = async () => {
            const resp = await request(`groups/${id}`)
            const group = resp.group
            setGroup(group)
        }
        getGroup()
    }, [])

    return (
        <>
        {group ? 
        <div>

            {group.createdBy === currentUser.username ?
                <div>
                    <button>Edit</button>
                    <button>Delete</button>
                </div>
                :
                <button>Leave Group</button>}
            {/* DELETE/EDIT BUTTONS IF YOU OWN THE GROUP */}
            {/* LEAVE BUTTON IF YOU DONT OWN THE GROUP */}
        <div className="GroupPage">
            {/* // group card with group information */}
            <div className="GroupPage-top">
                <img className="GroupPage-group-picture" src={group.groupPicture}></img>
                <p className="GroupPage-group-name">{group.name}</p>
                <div className="GroupPage-description"><i>{group.description}</i></div>
            </div>
            {/* Group tabs */}
        </div>
        <GroupTabs />
        </div>




            // tabs for posts/sets
        : <h1>Loading...</h1>}
        </>

    )
}

export default GroupPage;
