import React from 'react';
import { useFields } from '../utils/hooks';
import '../css/GroupEditCard.css'


const GroupEditCard = ({group, setEditMode, editGroup}) => {
    const INITIAL_STATE = {
        groupPicture: group.groupPicture,
        name: group.name,
        description: group.description
    }
    const [formData, handleChange] = useFields(INITIAL_STATE)

    return (
    <form className="GroupEditCard">
        <label className="GroupEditCard-label">Group Picture</label> <br></br>
        <input className="GroupEditCard-input" type="text" onChange={handleChange} value={formData.groupPicture}></input>

        <label className="GroupEditCard-label">Name</label> <br></br>
        <input className="GroupEditCard-input" type="text" onChange={handleChange} value={formData.name}></input>

        <label className="GroupEditCard-label">Description</label> <br></br>
        <input className="GroupEditCard-input" type="text" onChange={handleChange} value={formData.description}></input>

        <button className="GroupEditCard-button" onClick={() => setEditMode(false)}>Cancel</button> 
        <button className="GroupEditCard-button" onClick={() => editGroup(formData)}>Save Changes</button> 
    </form>
    )
}

export default GroupEditCard;
