import React, { useContext } from 'react';
import { useFields } from '../utils/hooks';
import '../css/NewPostForm.css';
import UserContext from '../utils/UserContext';

const NewPostForm = ({groupId}) => {
    const { currentUser, request, setCurrentUser } = useContext(UserContext)
    const INITIAL_STATE = {
        text: ''
    }
    const [formData, handleChange] = useFields(INITIAL_STATE)

    const addPost = async () => {
        request(`groups/${groupId}/posts`, formData)
        const user = await request(`users/${currentUser.username}`)
        setCurrentUser(user.user)

    }
    return (
       <>
        <form className="NewPostForm">
            <div>
                <textarea className="NewPostForm-input" type="textarea" name="text" value={formData.text} onChange={handleChange} placeholder="Make a post"></textarea>
            </div>
            <div>
                <button className="NewPostForm-post-button" onClick={addPost}>Post</button>
            </div>
        </form>
       </>
    )
}

export default NewPostForm;
