import React, { useState, useEffect, useContext } from 'react';
import UserCard from './UserCard';
import UserContext from '../utils/UserContext';
import Search from './Search';

const UsersPage = () => {
    const [users, setUsers] = useState()
    const [searchTerm, setSearchTerm] = useState()
    const { request } = useContext(UserContext)

    // get users on page load
    useEffect(() => {
        const getUsers = async () => {
            const resp = await request(`users`)
            const allUsers = resp.users
            setUsers(allUsers)
        }
        getUsers()
    }, [])

    return (
        <>
            {users ?
                    <>
                    <Search setSearchTerm={setSearchTerm} searchTerm={searchTerm} text="Search by username" />
                    {users.map(u => <UserCard key={u.username} user={u} />)}
                    </>

                    :
                    <h1>'Loading...'</h1>
            }
        </>
    )
}

export default UsersPage
