import React, { useContext, useState } from 'react';
import UserContext from '../utils/UserContext';

const UserProfileForm = () => {
    const { currentUser } = useContext(UserContext)
    const INITIAL_STATE = {
        username: currentUser.username,
        firstName: currentUser.firstName,
        email: currentUser.email,
        profilePicture: currentUser.profilePicture
    }
    const [formData, handleChange] = useState(INITIAL_STATE)
    return (
        <div className="Form-container">
            <h1 className="Form-h1"> Your Profile </h1>
            <form>
                <label className="Form-label">Username</label>
                <input className="Form-input" type="text" value={formData.username} disabled={true}></input>

                <label className="Form-label">Preferred name</label>
                <input className="Form-input" type="text" value={formData.firstName} onChange={handleChange}></input>

                <label className="Form-label">Email</label>
                <input className="Form-input" type="text" value={formData.email} onChange={handleChange}></input>

                <button className="Form-button">Save Changes</button>
            </form>
        </div>
    )
}

export default UserProfileForm;
