import React, { useState, useEffect, useContext } from 'react';
import UserCard from './UserCard';
import UserContext from '../utils/UserContext';
import Search from './Search';

const UsersPage = () => {
    const [users, setUsers] = useState()
    const [searchTerm, setSearchTerm] = useState('')
    const { request } = useContext(UserContext)
    const [showingUsers, setShowingUsers] = useState()

    // get users on page load
    useEffect(() => {
        const getUsers = async () => {
            const resp = await request(`users`)
            const allUsers = resp.users
            setUsers(allUsers)
            setShowingUsers(allUsers)
        }
        getUsers()
    }, [])

    useEffect(() => {
        if (users) {
            const usersToShow = users.filter(u => !u.username.toLowerCase().startsWith(searchTerm.toLowerCase()))
            console.log(usersToShow)
            setShowingUsers(usersToShow)
        }
    }, [searchTerm])

    return (
        <>
            {users ?
                    <>
                    <Search setSearchTerm={setSearchTerm} searchTerm={searchTerm} text="Search by username" />
                    {showingUsers.map(u => <UserCard key={u.username} user={u} />)}
                    </>

                    :
                    <h1>'Loading...'</h1>
            }
        </>
    )
}

export default UsersPage
