import React, { useState } from 'react';
import '../css/NewSetForm.css';
import { useFields } from '../utils/hooks';

const NewSetForm = () => {
    const [hidden, setHidden] = useState(false)
    const INITIAL_STATE = {
        name: '',
        description: '',
        hidden: hidden,
        sideOneName: 'Term',
        sideTwoName: 'Definition'
    }
    const [formData, handleChange] = useFields(INITIAL_STATE)
    return (
        <>
            <div className="NewSetForm-container">
                {console.log(formData.hidden)}
                <form>
                    <label>Set Name</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} />

                    <label>Description</label>
                    <input type="text" name="description" value={formData.description} onChange={handleChange} />

                    <label>Public</label>
                    <input type="radio" name="hidden" value={false} onChange={e => setHidden(e.target.value)} />

                    <label>Private</label>
                    <input type="radio" name="hidden" value={true} onChange={e => setHidden(e.target.value)} />

                    <label>Side One Name</label>
                    <input type="text" name="sideOneName" value={formData.sideOneName} onChange={handleChange} />

                    <label>Side Two Name</label>
                    <input type="text" name="sideTwoName" value={formData.sideTwoName} onChange={handleChange} />

                </form>
            </div>
        </>
    )
}

export default NewSetForm;
