import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import UserContext from '../utils/UserContext';
import "../css/Set.css";


const Set =  () => {
    const { id } = useParams()
    const { request, currentUser } = useContext(UserContext)
    const [set, setSet] = useState()
    const [flashcards, setFlashcards] = useState([])

    useEffect(() => {
        const getSet = async () => {
            const resp = await request(`sets/${id}`)
            setSet(resp.set)
        }
        getSet()
    }, [])



    // get set with that id
    // informational card on top
    // map flashcards into cards
    return (
        <>
            <div className="Set">
                <p className="Set-name">{set.name}</p>
                <p className="Set-description">{set.description}</p>
                <p className="Set-side-one">Side One: {set.sideOneName}</p>
                <p className="Set-side-two">Side Two: {set.sideTwoName}</p>
                {set.createdBy === currentUser.username? 
                <div className="Set-buttons">
                    <button>Edit Set</button>
                    <button>Delete Set</button>
                </div>
                : '' }
            </div>

        </>
    )
}

export default Set;
