import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { useFields } from '../utils/hooks';
import UserContext from '../utils/UserContext';

const NewGroupForm = () => {
    const { request, setCurrentUser, currentUser } = useContext(UserContext)
    const navigate = useNavigate()

    const INITIAL_STATE = {
        name: '',
        description: '',
        groupPicture: ''
    }

    const [formData, handleChange] = useFields(INITIAL_STATE)
    const handleSubmit = async (e) => {

        e.preventDefault()
        try {
            await request(`groups`, formData, 'post')
            const user = await request(`users/${currentUser.username}`)
            setCurrentUser(user.user)
            // update currentUser?
            navigate('/groups')
        } catch(e) {
            console.log(e)
        }
    }
    return (
        <>
            <div className="NewSetForm-container">
                <h1 className="NewSetForm-h1">Create a Group</h1>

                <form className="NewSetForm-form">
                    <label className="NewSetForm-label">Group Name</label>
                    <input className="NewSetForm-input" type="text" name="name" value={formData.name} onChange={handleChange} />

                    <label className="NewSetForm-label">Description</label>
                    <input className="NewSetForm-input" type="text" name="description" value={formData.description} onChange={handleChange} />

                    <label className="NewSetForm-label">Group Picture</label>
                    <input className="NewSetForm-input" type="text" name="groupPicture" value={formData.groupPicture} onChange={handleChange} />

                    <button className="NewSetForm-button">Create Group</button>
                </form>
            </div>
        </>

    )
}

export default NewGroupForm;
