import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';
import UserContext from '../utils/UserContext';
import '../css/UserSets.css';
import SetCard from './SetCard';

const UserSets = () => {
    const { currentUser } = useContext(UserContext)
    const sets = currentUser.sets
    console.log(sets)


    return (
        <>
            <div className="NewSetCard">
                <NavLink to="sets/new">
                    <button className="NewSetButton">+</button>
                    <p className="NewSetText">Create a new set</p>
                </NavLink>
            </div>
            <div>
                {sets.length === 0 ? <p className="NoNewSets"><i>No sets yet!</i></p>
                :
                sets.map(s => <SetCard className="SetCard" set={s} />)}
            </div>
        </>
        // create new set card
