import './css/App.css';
import jwt_decode from 'jwt-decode';
import axios from 'axios';
import React, { useState, useEffect} from 'react';
import Router from "./routes/Router";
import { BrowserRouter } from 'react-router-dom';
import Navbar from './components/Navbar';
import { useLocalStorage } from './utils/hooks';
import UserContext from './utils/UserContext';

const BASE_URL = process.env.REACT_APP_BASE_URL || "http://localhost:3001";

function App() {
    const [token, setToken] = useLocalStorage('token', null)
    const [currentUser, setCurrentUser] = useState(null)


    // on token change, get user info and store in current user
    useEffect(() => {
      async function getUser() {
        try {
          if (!token) return
          const payload = jwt_decode(token)
          const username = payload.username
          await request(`users/${username}`)
          const updatedUser = await request(`useres/${currentUser.username}`)
          setCurrentUser(updatedUser)
          return
        } catch { 
          return
        }
      }
      if (token) getUser()
    }, [token])

    async function request(endpoint, data = {}, method = "get") {
        console.debug("API Call:", endpoint, data, method);

        //there are multiple ways to pass an authorization token, this is how you pass it in the header.
        //this has been provided to show you another way to pass the token. you are only expected to read this code for this project.
        console.debug(data)
        const url = `${BASE_URL}/${endpoint}`;
        const headers = { Authorization: `Bearer ${token}` } 
        const params = (method === "get")
            ? data
            : {};

        try {
          return (await axios({ url, method, data, params, headers })).data;
        } catch (err) {
          console.log(err)
          console.error("API Error:", err.response);
          let message = err.response.data.error.message
          throw Array.isArray(message) ? message : [message];
        }
      }

   // get token, set token state 
   async function login({username, password}) {
    try {
        const resp = await axios.post(`auth/token`, {username, password})
        const token = resp.data.token
        setToken(token)
    } catch {
        return
    }
    }

    // clear user from state and token in localStorage
    function logout() {
      setCurrentUser(null)
      setToken(null)
    }

    // register new user and store their token in localStorage
    async function signup(user) {
        const resp = await axios.post(`auth/register`, user)
        const token = resp.data.token
        if (!token) return
        setToken(token)
     }

  return (
    <div className="App">
        <BrowserRouter>
            <UserContext.Provider value={{login, logout, signup, currentUser, setCurrentUser, token, request}}>
                <Navbar />
                <Router />
            </UserContext.Provider>
        </BrowserRouter>
    </div>
  );
}

export default App;
