import React from 'react';
import { useFields } from '../utils/hooks';


const GroupEditCard = ({group, setEditMode, editGroup}) => {
    const INITIAL_STATE = {
        groupPicture: group.groupPicture,
        name: group.name,
        description: group.description
    }
    const [formData, handleChange] = useFields(INITIAL_STATE)

    return (
    <form className="GroupPage-edit-form">
        <label>Group Picture</label>
        <input type="text" onChange={handleChange} value={formData.groupPicture}></input>

        <label>Name</label>
        <input type="text" onChange={handleChange} value={formData.groupPicture}></input>

        <label>Description</label>
        <input type="text" onChange={handleChange} value={formData.description}></input>

        <button onClick={() => setEditMode(false)}>Cancel</button> 
        <button onClick={() => editGroup(formData)}>Save Changes</button> 
    </form>
    )
}

export default GroupEditCard;
