import React from 'react';
import '../css/Post.css';

const Post = ({post}) => {
    return (
        <div className="Post">
            <div className="Post-body">
                <div className="Post-posted-by">{post.postedBy}</div>
                <div className="Post-text">{post.text}</div>
            </div>

            <button>Reply</button>
        </div>
    )
}

export default Post
