import React, { useState, useContext, useEffect } from 'react';
import UserContext from '../utils/UserContext';
import SetCard from './SetCard';
import Search from './Search';

const PublicSets = ({searchTerm, setSearchTerm}) => {
    const [sets, setSets] = useState()
    const [showingSets, setShowingSets] = []
    const { request } = useContext(UserContext)

    useEffect(() => {
        const getPublicSets = async () => {
            const resp = await request(`sets`)
            const sets = resp.sets
            setSets(sets)
            setShowingSets(sets)
        }
        getPublicSets()
    }, [])

    useEffect(() => {
        const setsToShow = sets.filter(s => s.name.startsWith(searchTerm))
        setShowingSets(setsToShow)
    }, [searchTerm])
    // on load, get all public sets?
    return (
        <>
        {sets ? 
            <div>
                <Search setSearchTerm={setSearchTerm}/>
                {showingSets.map(s => <SetCard set={s} key={s.id}/>)}
            </div>

            : <h1>Loading...</h1>}
        </>
    )
}

export default PublicSets;
