import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import UserContext from '../utils/UserContext';

const GroupPage = () => {
    const { id } = useParams()
    const [group, setGroup] = useState()
    const { request } = useContext(UserContext)

    // get group on page load
    useEffect(() => {
        const getGroup = async () => {
            const resp = await request(`groups/${id}`)
            const group = resp.group
            setGroup(group)
        }
        getGroup()
    }, [])

    return (
        <div className="GroupPage">
            <div className="GroupPage-top">
                <div>
                    <img src={group.groupPicture}></img>
                    <p>{group.name}</p>
                </div>
                <p>{group.description}</p>
            </div>
        </div>
            // group card with group information
            // tabs for posts/sets

    )
}

export default GroupPage;
